import React from 'react';

function App() {
  const name = '리액트';
  const style = {
    // background-color는 backgroundColor와 같이 -가 사라지고 카멜 표기법으로 작성됩니다.
    backgroundColor : 'black',
    color : 'aqua',
    fontSize : '48px',
    fontWeight : 'bold',
    padding : 16
  };
  return <div style = {style}>{name === '리액트' && <h1>리액트입니다.</h1>}</div> 
}

export default App;
